package Javabasics;

public class Forloop_Example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     for(int i=1;i<=5;i++)
     {
    	 int k=5; 
    	for(int j=1;j<=i;j++)
    	 {
    		 
    		 System.out.print(k);
    		 k--;
    	 }
    	 System.out.println();
     }
	}

}
